package com.hyy.ifm.util;


import com.alibaba.druid.util.StringUtils;
import sun.misc.BASE64Decoder;

import java.io.*;
import java.text.SimpleDateFormat;
import java.util.Date;

/**
 * 图片上传工具类
 * 
 * @author
 */
@SuppressWarnings("restriction")
public class ImageFileUtil {
    
    public static boolean uploadImageFile(String imgStr, String destPath, String filename)  
    {   
        //图像或地址数据为空
        if (StringUtils.isEmpty(imgStr) || StringUtils.isEmpty(destPath) || StringUtils.isEmpty(filename)){
            return false;  
        } 
        
        if (!destPath.substring(destPath.length() - 1).equals("/")) {
            System.out.println(1);
            destPath += "/";
        }
        File nfile = new File(destPath);
        if (!nfile.isDirectory()) {
            nfile.mkdirs();
        }
        String imgFilePath = destPath + filename;
        
        //对字节数组字符串进行Base64解码并生成图片  
        BASE64Decoder decoder = new BASE64Decoder();  
        try   
        {  
            //Base64解码  
            byte[] b = decoder.decodeBuffer(imgStr);  
            for(int i=0;i<b.length;++i)  
            {  
                if(b[i]<0)  
                {//调整异常数据  
                    b[i]+=256;  
                }  
            }  
            //生成jpeg图片  
            System.out.println(imgFilePath);
            OutputStream out = new FileOutputStream(imgFilePath);      
            out.write(b);  
            out.flush();  
            out.close();  
            return true;  
        }   
        catch (Exception e)   
        {  
            return false;  
        }  
    }  
    
    public void inputstreamtofile(InputStream ins, File file) {
        OutputStream os = null;
        try {
            os = new FileOutputStream(file);
            int bytesRead = 0;
            byte[] buffer = new byte[8192];
            while ((bytesRead = ins.read(buffer, 0, 8192)) != -1) {
                os.write(buffer, 0, bytesRead);
            }
        } catch (FileNotFoundException e) {
            e.printStackTrace();
        } catch (IOException e) {
            e.printStackTrace();
        }finally {
            if(null != os){
                try {
                    os.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            try {
                ins.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }
    
    public static void main(String[] args) {
        String base64Str = "";
        String destPath = "/fileUpload/upload/" + new SimpleDateFormat("yyyyMMdd").format(new Date())+"/"; 
        String newname = String.valueOf((new Date()).getTime()) + ".jpg";
        uploadImageFile(base64Str, destPath, newname);
    }
}
